import { Button } from "antd";
import { SlidingDiv,UsersDiv,SocialShareHeading,SocialShareFacebook, AutopostDiv,MaxScoreDiv,PostPerdayDiv,PostGap } from "./Component";
import { useState } from "react";
import { CheckOutlined, CloseOutlined } from '@ant-design/icons';
import { Switch, Space } from 'antd';
import { styled } from "styled-components";
import { Col, InputNumber, Row, Slider } from 'antd';
import { Input, Tooltip } from 'antd';
const SocialDiv = () => {
    const userData=[{
        name:"kuberan",
        autopost:"yes",
        score:1,
        posts:3,
        gap:"67"
    },
    {
        name:"venkatesh",
        autopost:"no",
        score:1,
        posts:3,
        gap:"67"
    }
    ]
    const  InputGap=styled(Input)`
    width:50px;
    height: 32px;
    `
    const [inputValue, setInputValue] = useState(1);
  const onChange = (newValue) => {
    setInputValue(newValue);
    const [isChecked, setIsChecked] = useState(false);

  const handleSwitchChange = (checked) => {
    setIsChecked(checked);
  };
  };
    return ( 
        <UsersDiv>
             {userData.map(user => (
          <li key={user.name}> {user.name} <button>change</button> </li>  ))}
            <SlidingDiv>
                <SocialShareHeading>Social Share Setting</SocialShareHeading>
                <SocialShareFacebook>

                    <AutopostDiv>
                        <p>Allow Autopost</p>
                        <Switch checkedChildren={<CheckOutlined />} unCheckedChildren={<CloseOutlined />} defaultChecked/>
                     </AutopostDiv>
                    <MaxScoreDiv>
                        <p>Minimum score to auto-post on social media</p>
                        <p>{inputValue}</p>
                        <Col span={10}>
                        <Slider
                        min={1}
                        max={5}
                        onChange={onChange}
                        value={typeof inputValue === 'number' ? inputValue : 0}
                        />
                        </Col>
                    </MaxScoreDiv>
                    <PostPerdayDiv>
                        <p>Maximum number of posts per day</p>
                        <Input/>
                    </PostPerdayDiv>
                    <PostGap>
                        <p>Minimum gap between posts</p>

                        <Space.Compact block>
                                <InputGap></InputGap>
                                <Button>Hours</Button>
                                <InputGap></InputGap>
                                <Button>Minutes</Button>
                        </Space.Compact>
                    </PostGap>

                </SocialShareFacebook>
            </SlidingDiv>

        </UsersDiv>
     );
}

export default SocialDiv;
