import { Button } from "antd";
import {
  SlidingDiv,
  UsersDiv,
  SocialShareHeading,
  SocialShareFacebook,
  AutopostDiv,
  MaxScoreDiv,
  PostPerdayDiv,
  PostGap,
} from "./Component";
import { useState } from "react";
import { CheckOutlined, CloseOutlined } from "@ant-design/icons";
import { Switch, Space } from "antd";
import { styled } from "styled-components";
import { Col, InputNumber, Row, Slider } from "antd";
import { Input, Drawer } from "antd";
const SocialDiv = () => {
  const [userData, setuserData] = useState([
    {
      name: "kuberan",
      autopost: false,
      score: 2,
      posts: 3,
      gaphours: 2,
      gapminutes: 13
    }
  ]);

  const InputGap = styled(Input)`
    width: 50px;
    height: 32px;
  `
    const Drawers = styled(Drawer)`

    `

  const [inputValue, setInputValue] = useState(1);
  const onChange = (newValue) => {
    setInputValue(newValue);
  };

  const [isChecked, setIsChecked] = useState(userData[0].autopost);

  const handleSwitchChange = (checked) => {
    setIsChecked(checked);
  };

  const [Posts, setPosts] = useState(0);
  const HandlePostChange = (e) => {
    console.log(e.target.value)
    setPosts(e.target.value);
  };
  const [hours,sethours]=useState(0)
  const [minutes,setminutes]=useState(0)
  const handleHours=(e)=>{
    sethours(e.target.value)
  }
  const handleminutes=(e)=>{
    setminutes(e.target.value)
  }

  const HandleSubmit = () => {
    let ChangeData = {
      name: "kuberan",
      autopost: isChecked,
      score: inputValue,
      posts: Posts,
      gaphours: hours,
      gapminutes: minutes
    }
    setuserData([ChangeData]);
  };


  const [open, setOpen] = useState(false);
  const showDrawer = () => {
    setOpen(true);
  };
  const onClose = () => {
    setOpen(false);
  };

  return (
    <UsersDiv>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Auto Post</th>
            <th>Min Score to post</th>
            <th>Post per day</th>
            <th>Gap between posts</th>
            <th>Settings</th>
          </tr>
        </thead>
        <tbody>
          {userData.map((item) => (
            <tr>
              <td>{item.name}</td>
              <td>{item.autopost.toString()}</td>
              <td>{item.score}</td>
              <td>{item.posts}</td>
              <td>{item.gaphours} Hours {item.gapminutes} minutes</td>
              <td><Button type="primary" onClick={showDrawer}>Settings</Button></td>
            </tr>
          ))}  
        </tbody>
      </table>

        <Drawers title="Basic Drawer" placement="right" onClose={onClose} open={open}>
        <SocialShareHeading><h3>Social Share Setting</h3></SocialShareHeading>
        <AutopostDiv>
            <p>Allow Autopost</p>
            <Switch
              checked={isChecked}
              onChange={handleSwitchChange}
              checkedChildren={<CheckOutlined />}
              unCheckedChildren={<CloseOutlined />}
              defaultChecked
            />
          </AutopostDiv>
        {
            isChecked?(
        <SocialShareFacebook>
          <MaxScoreDiv>
            <p>Minimum score to auto-post on social media</p>
            <p>{inputValue}</p>
            <Col span={10}>
              <Slider
                min={1}
                max={5}
                onChange={onChange}
                value={typeof inputValue === "number" ? inputValue : 0}
              />
            </Col>
          </MaxScoreDiv>
          <PostPerdayDiv>
            <p>Maximum number of posts per day</p>
            <Input onChange={HandlePostChange} />
          </PostPerdayDiv>
          <PostGap>
            <p>Minimum gap between posts</p>

            <Space.Compact block>
              <InputGap onChange={handleHours}></InputGap>
              <Button>Hours</Button>
              <InputGap onChange={handleminutes}></InputGap>
              <Button>Minutes</Button>
            </Space.Compact>
          </PostGap>

        </SocialShareFacebook>)
        :(<p>please put on the autopost</p>)
        }
        <div>
            <Button type="primary" onClick={HandleSubmit}>
              Update
            </Button>
          </div>
        </Drawers>

    </UsersDiv>
  );
};

export default SocialDiv;
